import React from "react";
import { Map, Marker, Popup, TileLayer } from "react-leaflet";
import { Icon } from "leaflet";
import Select from 'react-select'
import "./App.css";
import useSwr from "swr";
import 'bootstrap/dist/css/bootstrap.min.css';

const fetcher = (...args) => fetch(...args).then(response => response.json());
const ratings = [
  { label: "1<", value: 1 },
  { label: "2<", value: 2 },
  { label: "3<", value: 3 },
  { label: "4<", value: 4 },
  { label: "5<", value: 5 },
];
export const icon = new Icon({
  iconUrl: "/mountain.svg",
  iconSize: [25, 25]
});

const App = () => {
  const [activeClimb, setActiveClimb] = React.useState(null);
  const url = 'https://www.mountainproject.com/data/get-routes-for-lat-lon?lat=40.65&lon=-111.91&maxDistance=10&minDiff=5.6&maxDiff=5.10&key=200703063-2c86cd159070d7b2b2f9abc830c4f44b';
  const { data, error } = useSwr(url, { fetcher });

  const climbs = data && !error ? data.routes : [];
  return (
    <div>
      <Select options={ratings} />
    <Map center={[40.65, -111.91]} zoom={12}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
      />

      {climbs.map(climb => (
        <Marker
          key={climb.id}
          position={[climb.latitude, climb.longitude]}
          onClick={() => {
            setActiveClimb(climb);
          }}
          icon={icon}
        />
      ))}

      {activeClimb && (
        <Popup
          position={[
            activeClimb.latitude,
            activeClimb.longitude
          ]}
          onClose={() => {
            setActiveClimb(null);
          }}
        >
          <div>
            <h2>{activeClimb.name}</h2>
            <p>{activeClimb.rating}</p>
          </div>
        </Popup>
      )}
    </Map>
    </div>

    // <Map center={[52.6376, -1.135171]} zoom={12}>
    //   <TileLayer
    //     url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
    //     attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
    //   />

    //   {fires.map(fire => (
    //     <Marker
    //       key={fire.id}
    //       position={[fire.latitude, fire.longitude]}
    //     />
    //   ))}
    // </Map>
  );
}
export default App
