import React from "react";
import { Map, Marker, Popup, TileLayer } from "react-leaflet";
import { Icon } from "leaflet";
import Select from 'react-select'
import "./App.css";
import useSwr from "swr";

const fetcher = (...args) => fetch(...args).then(response => response.json());
const StarRatings = [
  { label: "All routes", value: 0 },
  { label: "1+ of 4 stars", value: 1 },
  { label: "2+ of 4 stars", value: 2 },
  { label: "3+ of 4 stars", value: 3 },
  { label: "4 of 4 stars", value: 4 }
];
const Types = [
  { label: "All types", value: '' },
  { label: "Trad", value: 'Trad' },
  { label: "Sport", value: 'Sport' },
  { label: "Toprope", value: 'TR' }
];
export const icon = new Icon({
  iconUrl: "/mountain.svg",
  iconSize: [25, 25]
});

const App = () => {
  const url = 'https://www.mountainproject.com/data/get-routes-for-lat-lon?lat=40.65&lon=-111.91&maxDistance=10&minDiff=4.0&maxDiff=5.9&key=200703063-2c86cd159070d7b2b2f9abc830c4f44b';
  const { data, error } = useSwr(url, { fetcher });
  const climbs = data && !error ? data.routes : [];
  const [filterClimbs, setFilterClimbs] = React.useState([]);
  const [activeClimb, setActiveClimb] = React.useState(null);
  const [stars, setStars] = React.useState({ label: "All routes", value: 0 });
  React.useEffect(() => {
    // Should not ever set state during rendering, so do this in useEffect instead.
    setFilterClimbs(climbs);
  }, [climbs]);
  const [type, setType] = React.useState('');

  const onChangeStars = (e) => {
    //const newClimbs = climbs.filter(x => Math.round(x.stars) === e.value && x.rating >= minDiff && x.rating >= maxDiff)
    const newClimbs = climbs.filter(x => x.stars >= e.value /*&& type in x.type*/)
    setFilterClimbs(newClimbs)
    console.log(e.label, e.value)
    setStars(e);
  };
  const onChangeType = (e) => {
    const newClimbs = climbs.filter(x => e.value in x.type)
    setFilterClimbs(newClimbs)
    console.log(e.label, e.value)
    setType(e);
  };
  return (
    <div>
      <div className="filterMountains">
        <Select options={StarRatings} value={stars} onChange={onChangeStars}/>
        <Select options={Types} value={type} onChange={onChangeType}/>

      </div>
      <Map center={[40.65, -111.91]} zoom={12}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
        />
        {filterClimbs.map(climb => (
          <Marker
            key={climb.id}
            position={[climb.latitude, climb.longitude]}
            onClick={() => {
              setActiveClimb(climb);
            }}
            icon={icon}
          />
        ))}

        {activeClimb && (
          <Popup
            position={[
              activeClimb.latitude,
              activeClimb.longitude
            ]}
            onClose={() => {
              setActiveClimb(null);
            }}
          >
            <div>
              <h2>{activeClimb.name}</h2>
              <p>{activeClimb.rating}</p>
            </div>
          </Popup>
        )}
      </Map>
    </div>
  );
}
export default App
